mapper.select:
select * from t_adminusers
where isDeleted=false
 and id=#{id} 
 and workId=#{workId} 
 and name=#{name} 
 and phone=#{phone} 
 and isChecked=#{isChecked}

select u.id adminUserId, u.`name` adminUserName, r.id roleId, r.`name` roleName, u.workId workId, u.phone phone from t_adminusers u
left join t_adminuserroles ur on u.id=ur.adminUserId
left join t_roles r on r.id=ur.roleId
where u.isDeleted=false
 and u.id=#{adminUserId} 
 and u.name=#{adminUserName} 
 and r.id=#{roleId} 
 and r.name=#{roleName} 
 and u.workId=#{workId}
 and u.phone=#{phone}

select adu.id adminUserId, adu.`name` adminUserName, r.id roleId, r.`name` roleName, adu.workId workId, adu.phone phone from t_adminusers adu left join t_adminuserroles adur on adu.id=adur.adminUserId left join t_roles r on r.id=adur.roleId left join t_classadminusers cad on adu.id=cad.adminUserId where r.id = #{nextRole.id}
and cad.classId in
#{item.id}
