mapper.select:
select * from t_adminusers
where isDeleted=false
 and id=#{id} 
 and workId=#{workId} 
 and name=#{name} 
 and phone=#{phone} 
 and isChecked=#{isChecked}

select u.id id, u.`name` name, r.id roleId, r.`name` roleName from t_adminusers u
left join t_adminuserroles ur on u.id=ur.adminUserId
left join t_roles r on r.id=ur.roleId
where u.isDeleted=false
 and u.id=#{id} 
 and u.name=#{name} 
 and r.id=#{roleId} 
 and r.name=#{roleName}
